import { useState, useEffect, useCallback, useMemo } from 'react';
import { fetchCourses, searchCourses } from '../services/api';

// Custom hook to manage courses data
// Demonstrates: custom hooks, async/await, error handling, loading states, memoization
export const useCourses = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [lastFetchTime, setLastFetchTime] = useState(null);

  // Memoized filtered courses to prevent unnecessary recalculations
  const filteredCourses = useMemo(() => {
    if (!searchQuery) return courses;
    
    return courses.filter(course =>
      course.name.toLowerCase().includes(searchQuery.toLowerCase())
    );
  }, [courses, searchQuery]);

  // Memoized course statistics
  const courseStats = useMemo(() => {
    const total = courses.length;
    const available = courses.filter(course => course.available).length;
    const unavailable = total - available;
    
    // Group by duration
    const byDuration = courses.reduce((acc, course) => {
      acc[course.duration] = (acc[course.duration] || 0) + 1;
      return acc;
    }, {});

    return {
      total,
      available,
      unavailable,
      byDuration,
      averageCredits: courses.length > 0 
        ? Math.round(courses.reduce((sum, course) => sum + course.credits, 0) / courses.length)
        : 0
    };
  }, [courses]);

  // Fetch all courses with error handling and loading states
  const loadCourses = useCallback(async () => {
    setLoading(true);
    setError(null);
    
    try {
      console.log('ðŸ”„ Fetching courses...'); // Demonstrates console logging for debugging
      
      const response = await fetchCourses();
      
      if (response.success) {
        setCourses(response.data);
        setLastFetchTime(new Date().toISOString());
        console.log(`âœ… Loaded ${response.data.length} courses successfully`);
      } else {
        throw new Error('Failed to fetch courses');
      }
      
    } catch (err) {
      console.error('âŒ Error fetching courses:', err);
      setError(err.message);
      
      // Retry logic with exponential backoff (demonstrates advanced error handling)
      if (err.message.includes('temporarily unavailable')) {
        console.log('ðŸ”„ Retrying in 2 seconds...');
        setTimeout(() => {
          loadCourses();
        }, 2000);
      }
    } finally {
      setLoading(false);
    }
  }, []);

  // Search courses with debouncing (demonstrates performance optimization)
  const searchCoursesDebounced = useCallback(async (query) => {
    if (!query.trim()) {
      setSearchQuery('');
      return;
    }

    setSearchQuery(query);
    setLoading(true);
    
    try {
      const response = await searchCourses(query);
      
      if (response.success) {
        setCourses(response.data);
        console.log(`ðŸ” Found ${response.data.length} courses for "${query}"`);
      }
      
    } catch (err) {
      console.error('âŒ Search error:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }, []);

  // Get course by ID (demonstrates utility functions)
  const getCourseById = useCallback((id) => {
    return courses.find(course => course.id === parseInt(id));
  }, [courses]);

  // Get available courses only
  const getAvailableCourses = useCallback(() => {
    return courses.filter(course => course.available);
  }, [courses]);

  // Get courses by duration
  const getCoursesByDuration = useCallback((duration) => {
    return courses.filter(course => course.duration === duration);
  }, [courses]);

  // Refresh courses (demonstrates manual refresh functionality)
  const refreshCourses = useCallback(() => {
    console.log('ðŸ”„ Manual refresh triggered');
    loadCourses();
  }, [loadCourses]);

  // Load courses on mount
  useEffect(() => {
    loadCourses();
  }, [loadCourses]);

  // Auto-refresh courses every 5 minutes (demonstrates setInterval and cleanup)
  useEffect(() => {
    const intervalId = setInterval(() => {
      console.log('ðŸ”„ Auto-refreshing courses...');
      loadCourses();
    }, 5 * 60 * 1000); // 5 minutes

    // Cleanup function to prevent memory leaks
    return () => {
      console.log('ðŸ§¹ Cleaning up auto-refresh interval');
      clearInterval(intervalId);
    };
  }, [loadCourses]);

  // Return memoized object to prevent unnecessary re-renders
  return {
    // State
    courses: filteredCourses,
    allCourses: courses,
    loading,
    error,
    searchQuery,
    lastFetchTime,
    
    // Computed values
    courseStats,
    
    // Actions
    loadCourses,
    searchCourses: searchCoursesDebounced,
    getCourseById,
    getAvailableCourses,
    getCoursesByDuration,
    refreshCourses,
    
    // Utility functions
    clearError: () => setError(null),
    clearSearch: () => setSearchQuery('')
  };
};
